window.addEventListener("load",function(){const e=document.querySelector(".js-header-button"),t=document.querySelector(".js-header-nav");e.addEventListener("click",function(){this.classList.toggle("is-open"),t.classList.toggle("is-open")})});const headerHeight=document.querySelector("header").offsetHeight,headerButton=document.querySelector(".js-header-button"),headerNav=document.querySelector(".js-header-nav");document.querySelectorAll('a[href*="#"]').forEach(e=>{e.addEventListener("click",function(t){t.preventDefault();const o=e.getAttribute("href"),n=o.substring(o.indexOf("#")),r=document.getElementById(n.replace("#","")),s=r.getBoundingClientRect().top+window.scrollY-headerHeight;window.scrollTo({top:s,behavior:"smooth"}),headerButton.classList.remove("is-open"),headerNav.classList.remove("is-open")})});